import React, { useMemo } from "react";
import { useNavigate } from "react-router-dom";
import { NameDropdown } from "../components";

export default function Home({
  data,
  selectedName,
  setSelectedName,
  selectedSetor,
  setSelectedSetor,
  selectedMes,
  setSelectedMes
}) {
  const navigate = useNavigate();

  const colaboradores = [
    { id: 1, nome: "Ana" },
    { id: 2, nome: "Carlos" },
    { id: 3, nome: "Mariana" }
  ];

  const uniqueNames = useMemo(() => {
    const names = new Set();
    data.forEach((item) => item.Nome && names.add(item.Nome));
    return Array.from(names).sort();
  }, [data]);

  const uniqueSetores = useMemo(() => {
    const setores = new Set();
    data.forEach((item) => item.Setor && setores.add(item.Setor));
    return Array.from(setores).sort();
  }, [data]);

  const uniqueMeses = useMemo(() => {
    const meses = new Set();
    data.forEach((item) => item.Mes && meses.add(item.Mes));
    return Array.from(meses).sort();
  }, [data]);

  return (
    <div style={{ paddingBottom: "24px" }}>
      <h3 style={{ marginBottom: "12px" }}>Acesso Rápido</h3>

      {colaboradores.map((colab) => (
        <div
          key={colab.id}
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            backgroundColor: "#f3f3f3",
            padding: "12px",
            borderRadius: "8px",
            marginBottom: "12px"
          }}
        >
          <span style={{ fontWeight: "bold" }}>{colab.nome}</span>
          <button
            onClick={() =>
              navigate(`/registrar/${colab.id}`, {
                state: { colaborador: colab }
              })
            }
            style={{
              padding: "6px 12px",
              fontSize: "14px",
              backgroundColor: "#4caf50",
              color: "#fff",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            Registrar Informações
          </button>
        </div>
      ))}

      <h3 style={{ margin: "24px 0 12px" }}>Filtros Avançados</h3>

      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          gap: "24px",
          marginBottom: "24px",
          alignItems: "flex-start"
        }}
      >
        <div style={{ flex: "1 1 240px" }}>
          <NameDropdown
            names={uniqueNames}
            selected={selectedName}
            onChange={(e) => setSelectedName(e.target.value)}
          />
        </div>

        <div style={{ flex: "1 1 240px" }}>
          <NameDropdown
            names={uniqueSetores}
            selected={selectedSetor}
            onChange={(e) => setSelectedSetor(e.target.value)}
          />
        </div>

        <div style={{ flex: "1 1 240px" }}>
          <NameDropdown
            names={uniqueMeses}
            selected={selectedMes}
            onChange={(e) => setSelectedMes(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
}
