import { useLocation } from 'react-router-dom';
import { useState } from 'react';

const Registro = () => {
  const { state } = useLocation();
  const colaborador = state?.colaborador;
  const [info, setInfo] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!info.trim()) {
      alert('Por favor, preencha a informação antes de salvar.');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('https://sheetdb.io/api/v1/YOUR_SHEET_ID', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          data: { nome: colaborador.nome, info }
        })
      });

      if (!response.ok) throw new Error('Erro ao registrar os dados');
      alert('Informação registrada com sucesso!');
      setInfo('');
    } catch (error) {
      alert(`Ocorreu um erro: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Registrar para {colaborador?.nome}</h2>
      <textarea
        value={info}
        onChange={(e) => setInfo(e.target.value)}
        placeholder="Digite a informação aqui..."
        rows={4}
        cols={50}
      />
      <br />
      <button onClick={handleSubmit} disabled={loading}>
        {loading ? 'Salvando...' : 'Salvar'}
      </button>
    </div>
  );
};

export default Registro;
