import { useLocation } from 'react-router-dom';
import { useState } from 'react';

const Registro = () => {
  const { state } = useLocation();
  const colaborador = state?.colaborador;
  const [info, setInfo] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!info.trim()) {
      alert('Por favor, preencha a informação antes de salvar.');
      return;
    }

    setLoading(true);

    try {
      const response = await fetch(
        'https://script.google.com/macros/s/AKfycbwMBQgcmlJ0ZzDf2u1UjI6E3wxGBm1DGYU-Hg0drLHoZB7PP0MXE8ArV6zf8Va0VBBn/exec',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            nome: colaborador.nome,
            info: info,
          }),
        }
      );

      if (!response.ok) throw new Error('Erro ao registrar os dados');
      alert('Informação registrada com sucesso!');
      setInfo('');
    } catch (error) {
      alert(`Ocorreu um erro ao salvar: ${error.message}`);
      console.error('Erro completo:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Registrar para {colaborador?.nome}</h2>
      <textarea
        value={info}
        onChange={(e) => setInfo(e.target.value)}
        placeholder="Digite a informação aqui..."
        rows={4}
        cols={50}
        style={{ marginBottom: '12px' }}
      />
      <br />
      <button onClick={handleSubmit} disabled={loading}>
        {loading ? 'Salvando...' : 'Salvar'}
      </button>
    </div>
  );
};

export default Registro;
